    import React, { useState } from 'react';
import { IntlProvider } from 'react-intl';
import translations from "./i18n/pt_br/translations";
import 'bootstrap/dist/css/bootstrap.min.css';
import { ThemeProvider } from 'styled-components';
import { lightTheme, darkTheme } from './themes/theme';
import { GlobalStyles } from './themes/global';
import { Header } from "./components";
import Container from 'react-bootstrap/Container';
import CreateRoom from './layouts/CreateRoom';
import Main from "./layouts/Main";
import Room from "./layouts/Room";
import NoMatch from "./layouts/NoMatch";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

function App(){
    const [theme, setTheme] = useState('light');

    const toggleTheme = () => {
        if(theme === "light"){
            setTheme("dark");
        }else{
            setTheme("light");
        }
    }

    const onChangeLang = (e) => {
        console.log("OK", e.target.value);
    }

    return (
        <ThemeProvider theme={theme === 'light' ? lightTheme : darkTheme}>
            <GlobalStyles />
            <IntlProvider messages={translations} locale="pt-br" defaultLocale="en">
                <Header onChangeLang={onchange} toggleTheme={ toggleTheme } />
                <Container fluid>
                    <Router>
                        <Switch>
                            <Route exact path="/"><Main /></Route>
                            <Route path="/newRoom"><CreateRoom /></Route>
                            <Route path="/room/:roomUUID"><Room /></Route>
                            <Route path="*"><NoMatch /></Route>
                        </Switch>
                    </Router>
                </Container>
            </IntlProvider>
        </ThemeProvider>
    );
}

export default App;
